// Clean version for final assembly.


// Clean helper
const clean = (str) => String(str || "").replace(/[^a-zA-Z0-9_-]/g, "");

// Random helper
const randomItem = (arr) => arr[Math.floor(Math.random() * arr.length)];

// ----------------------------
//  Phase Sync Listener
// ----------------------------
const attachRoomListener = (db, room, setters) => {
  const {
    setPlayers,
    setPhase,
    setMode,
    setCreator,
    setRound,
    setCurrentPrompt,
    setCurrentItem,
    setTurnIndex,
    setTimerEnd
  } = setters;

  const r = ref(db, "rooms/" + room);

  return onValue(r, (snap) => {
    const d = snap.val();
    if (!d) return;

    setPlayers(d.players || {});
    setPhase(d.phase || "lobby");
    setMode(d.mode || "liar");
    setCreator(d.creator || "");
    setRound(d.round || 1);
    setCurrentPrompt(d.currentPrompt || null);
    setCurrentItem(d.currentItem || null);
    setTurnIndex(d.turnIndex ?? 0);
    setTimerEnd(d.timerEnd || null);
  });
};

// ----------------------------
//  Timer Tick
// ----------------------------
const startTimer = (timerEnd, onTick, onTimeout) => {
  if (!timerEnd) return null;

  const id = setInterval(() => {
    const remain = Math.max(0, Math.ceil((timerEnd - Date.now()) / 1000));
    onTick(remain);
    if (remain <= 0) onTimeout();
  }, 1000);

  return id;
};

// ----------------------------
//  Phase Timeout Logic
// ----------------------------
const handlePhaseTimeout = async (db, room) => {
  const r = ref(db, "rooms/" + room);
  const snap = await get(r);
  if (!snap.exists()) return;

  const data = snap.val();

  switch (data.phase) {
    case "answer":
      await update(r, {
        phase: "debate",
        timerEnd: Date.now() + 120000
      });
      break;

    case "debate":
      await update(r, {
        phase: "reveal",
        timerEnd: null
      });
      break;

    case "oneword_turns":
      await update(r, {
        phase: "oneword_vote",
        timerEnd: Date.now() + 60000
      });
      break;

    case "oneword_vote":
      await update(r, {
        phase: "reveal",
        timerEnd: null
      });
      break;

    default:
      break;
  }
};

// ----------------------------
//  Start Liar Round
// ----------------------------
const startLiarRound = async (db, room, players, PROMPTS, creator, currentUser) => {
  if (creator !== currentUser) return;

  const names = Object.keys(players);
  if (names.length < 2) return;

  const r = ref(db, "rooms/" + room);

  const prompt = randomItem(PROMPTS);

  const impCount = Math.floor(Math.random() * names.length);
  const shuffled = [...names].sort(() => Math.random() - 0.5);
  const impostors = shuffled.slice(0, impCount);

  const updated = {};
  names.forEach((p) => {
    const isImp = impostors.includes(p);

    updated[p] = {
      ...players[p],
      answer: "",
      variant: isImp ? randomItem(prompt.impostors) : prompt.real,
      vote: [],
      submitted: false,
      voteSubmitted: false
    };
  });

  await update(r, {
    phase: "answer",
    currentPrompt: prompt.real,
    players: updated,
    timerEnd: Date.now() + 60000
  });
};

// ----------------------------
//  Submit Answer (Liar Mode)
// ----------------------------
const submitAnswer = async (db, room, name, answer, players) => {
  const r = ref(db, "rooms/" + room);

  await update(child(r, "players/" + name), {
    answer,
    submitted: true
  });

  const allDone = Object.values(players).every((p) => p.submitted);
  if (allDone) {
    await update(r, {
      phase: "debate",
      timerEnd: Date.now() + 120000
    });
  }
};

// ----------------------------
//  Submit Vote (One-Time Lock)
// ----------------------------
const submitVote = async (db, room, name, voteList, players) => {
  const r = ref(db, "rooms/" + room);
  const userRef = child(r, "players/" + name);

  if (players[name]?.voteSubmitted) return;

  await update(userRef, {
    vote: voteList,
    voteSubmitted: true
  });

  const allVoted = Object.values(players).every((p) => p.voteSubmitted);
  if (allVoted) {
    await update(r, {
      phase: "reveal",
      timerEnd: null
    });
  }
};

