// CHUNK 5 ‚Äî SCORE SYSTEM + IMPOSTOR LOGIC + END GAME PANEL
// This module is used inside the full App.jsx assembly.
// Includes awarding points, impostor rewards, reveal calculations,
// and persistent end-game panel UI elements.

const computeScores = (players, impostors) => {
  const updated = {};

  Object.entries(players).forEach(([name, p]) => {
    let score = p.score || 0;

    const votedForImpostor = (p.vote || []).some(v => impostors.includes(v));

    if (votedForImpostor) {
      score += 100;
    }

    updated[name] = { ...p, score };
  });

  const correctlyGuessed = {};
  Object.keys(players).forEach(p => (correctlyGuessed[p] = false));

  Object.entries(players).forEach(([voter, data]) => {
    if ((data.vote || []).some(v => impostors.includes(v))) {
      correctlyGuessed[voter] = true;
    }
  });

  impostors.forEach(name => {
    if (!Object.values(correctlyGuessed).includes(true)) {
      let score = updated[name].score || 0;
      score += 200;
      updated[name].score = score;
    }
  });

  return updated;
};

